import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import api from '../config/axios';
import './SubmitGrievance.css';

const SubmitGrievance = () => {
  const [formData, setFormData] = useState({
    subject: '',
    category: '',
    description: '',
    priority: 'medium'
  });
  const [files, setFiles] = useState([]);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
    setError('');
  };

  const handleFileChange = (e) => {
    const selectedFiles = Array.from(e.target.files);
    if (selectedFiles.length > 5) {
      setError('Maximum 5 files allowed');
      return;
    }

    // Check file sizes (10MB each)
    const maxSize = 10 * 1024 * 1024; // 10MB
    const oversizedFiles = selectedFiles.filter(file => file.size > maxSize);
    if (oversizedFiles.length > 0) {
      setError('File size should be less than 10MB');
      return;
    }

    setFiles(selectedFiles);
    setError('');
  };

  const removeFile = (index) => {
    setFiles(files.filter((_, i) => i !== index));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');

    if (!formData.subject || !formData.category || !formData.description) {
      setError('Please fill in all required fields');
      return;
    }

    setLoading(true);

    try {
      // Create FormData for file upload
      const submitData = new FormData();
      submitData.append('subject', formData.subject);
      submitData.append('category', formData.category);
      submitData.append('description', formData.description);
      submitData.append('priority', formData.priority);

      // Append files
      files.forEach((file) => {
        submitData.append('attachments', file);
      });

      // Get token for authorization
      const token = localStorage.getItem('token');

      // Get base URL
      const isProduction = window.location.hostname !== 'localhost';
      const baseURL = isProduction
        ? (process.env.REACT_APP_API_URL || 'https://git-grievance-system-backend.onrender.com')
        : 'http://localhost:5001';

      const response = await fetch(`${baseURL}/api/grievances`, {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${token}`
        },
        body: submitData
      });

      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.message || 'Failed to submit grievance');
      }

      setSuccess('Grievance submitted successfully!');
      setFiles([]);
      setTimeout(() => {
        navigate('/my-grievances');
      }, 1500);
    } catch (error) {
      setError(error.message || 'Failed to submit grievance');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="container">
      <div className="submit-grievance">
        <h1>Submit New Grievance</h1>
        {error && (
          <div className="alert alert-error">
            <i className="fas fa-exclamation-circle"></i> {error}
          </div>
        )}
        {success && (
          <div className="alert alert-success">
            <i className="fas fa-check-circle"></i> {success}
          </div>
        )}
        <div className="card">
          <form onSubmit={handleSubmit}>
            <div className="form-group">
              <label htmlFor="subject">Subject *</label>
              <input
                type="text"
                id="subject"
                name="subject"
                className="form-control"
                value={formData.subject}
                onChange={handleChange}
                required
                placeholder="Enter grievance subject"
              />
            </div>
            <div className="form-group">
              <label htmlFor="category">Category *</label>
              <select
                id="category"
                name="category"
                className="form-control"
                value={formData.category}
                onChange={handleChange}
                required
              >
                <option value="">Select Category</option>
                <option value="academic">Academic</option>
                <option value="administrative">Administrative</option>
                <option value="infrastructure">Infrastructure</option>
                <option value="hostel">Hostel</option>
                <option value="library">Library</option>
                <option value="examination">Examination</option>
                <option value="other">Other</option>
              </select>
            </div>
            <div className="form-group">
              <label htmlFor="priority">Priority</label>
              <select
                id="priority"
                name="priority"
                className="form-control"
                value={formData.priority}
                onChange={handleChange}
              >
                <option value="low">Low</option>
                <option value="medium">Medium</option>
                <option value="high">High</option>
                <option value="urgent">Urgent</option>
              </select>
            </div>
            <div className="form-group">
              <label htmlFor="description">Description *</label>
              <textarea
                id="description"
                name="description"
                className="form-control"
                value={formData.description}
                onChange={handleChange}
                required
                rows="8"
                placeholder="Describe your grievance in detail..."
              ></textarea>
            </div>
            <div className="form-group">
              <label htmlFor="attachments">
                <i className="fas fa-paperclip"></i> Attach Files (Optional)
              </label>
              <input
                type="file"
                id="attachments"
                name="attachments"
                className="form-control"
                onChange={handleFileChange}
                multiple
                accept="image/*,.pdf,.doc,.docx,.xls,.xlsx,.txt,.zip,.rar"
              />
              <small style={{ color: '#6b7280', fontSize: '12px', display: 'block', marginTop: '5px' }}>
                Maximum 5 files, 10MB each. Allowed: Images, PDF, Documents, Archives
              </small>
              {files.length > 0 && (
                <div style={{ marginTop: '10px' }}>
                  <strong>Selected Files:</strong>
                  <ul style={{ listStyle: 'none', padding: 0, marginTop: '5px' }}>
                    {files.map((file, index) => (
                      <li key={index} style={{ 
                        display: 'flex', 
                        justifyContent: 'space-between', 
                        alignItems: 'center',
                        padding: '5px 10px',
                        background: '#f3f4f6',
                        marginBottom: '5px',
                        borderRadius: '4px'
                      }}>
                        <span style={{ fontSize: '13px' }}>
                          <i className="fas fa-file"></i> {file.name} ({(file.size / 1024 / 1024).toFixed(2)} MB)
                        </span>
                        <button
                          type="button"
                          onClick={() => removeFile(index)}
                          style={{
                            background: '#ef4444',
                            color: 'white',
                            border: 'none',
                            borderRadius: '4px',
                            padding: '2px 8px',
                            cursor: 'pointer',
                            fontSize: '12px'
                          }}
                        >
                          <i className="fas fa-times"></i> Remove
                        </button>
                      </li>
                    ))}
                  </ul>
                </div>
              )}
            </div>
            <div className="form-actions">
              <button type="submit" className="btn btn-primary" disabled={loading}>
                {loading ? 'Submitting...' : 'Submit Grievance'}
              </button>
              <button
                type="button"
                className="btn btn-secondary"
                onClick={() => navigate('/dashboard')}
              >
                Cancel
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default SubmitGrievance;
